VALUES (@parent_id,  0,  22 /* StampQuest */, 0, 1, NULL, 'ApostateFinaleStarted_0910', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  1,  10 /* Tell */, 0.5, 1, NULL, 'Greetings!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  2,  10 /* Tell */, 0.5, 1, NULL, 'I have been sent here to ask adventurers, such as yourself, to aid in a rather delicate situation my Lord Asheron has found himself in.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  3,  10 /* Tell */, 0.5, 1, NULL, 'He has learned that the disruptions endangering a small pocket of located Empyreans are tied to a set of growing Temporal anomalies, as well as direct interference by these ''Apostate'' Virindi.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  4,  10 /* Tell */, 0.5, 1, NULL, 'To that end, he has located a confluence of overlapping temporal and portalspace disturbances in the Iron Blade fortifications to the north of here, as well as a defended portal in the caves of the Apostate Simulacra.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  5,  10 /* Tell */, 0.5, 1, NULL, 'There are several...points, of greater disturbance within the Iron Blade''s tunnels. We originally thought them to be due to the high levels of Aetherium, combined with the methods the Iron Blade Primus is using to control the portal pathway to his or her homeworld, but there are temporal anomalies mixed into the disturbances that lead my Lord to believe that they are related to the Portalspace that have prevented him from simply removing the group of Empyreans he has located...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  6,  10 /* Tell */, 0.5, 1, NULL, 'I have been given the means to allow you passage through the defended portal, but from there, it will be up to you to locate and end the disturbances, so my Lord Asheron may stabilize the area around the located Empyreans, and extract them from their present circumstances.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  7,  10 /* Tell */, 0.5, 1, NULL, 'My apologies, I do ramble on sometimes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  8,  10 /* Tell */, 0.5, 1, NULL, 'The short form. There''s a portal in the simulacra cave we believe to be key to ending the disturbance. I can protect you from the barriers and wards upon it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id,  9,  10 /* Tell */, 0.5, 1, NULL, 'In there, hopefully, you''ll find the source of the problem, or something that will lead you through the points of greatest disturbance in the Iron Blade Tunnels.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id, 10,  10 /* Tell */, 0.5, 1, NULL, 'Find the force or entity responsible, end it. Return to me with proof, and I''ll reward you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0)
     , (@parent_id, 11,  10 /* Tell */, 0.5, 1, NULL, 'Any questions?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1, 0, 0, 0);

