VALUES (@parent_id,  0,  64 /* TellFellow */, 0, 1, NULL, 'Hmmm... Winterborn sent you. I smell him on you and your friends.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  1,  64 /* TellFellow */, 1, 1, NULL, 'Perhaps... Perhaps you can help. I trust you. I trust your friends.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  2,  64 /* TellFellow */, 1, 1, NULL, 'There is Banderling, named Haraag. He changed recently. He changed a lot. Bigger, stronger, different. He come and try to get Tribes to follow him. Some choose to follow, we chose to fight. We drove him off. Chased him to cave. Then, he ripped a hole in the world and fled through it. Those who chased him through hole, did not return.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  3,  64 /* TellFellow */, 1, 1, NULL, 'Now, those who choose Haraag come and steal artifacts of the Banderlings. If he get enough, he will use to get others to follow him. I want you all to retrieve those artifacts.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  4,  64 /* TellFellow */, 1, 1, NULL, 'Near here, at cave to the east, you will find the hole in the world. Like those things you call portals, but worse. I have learned trick to protect you from hole while you travel to its end-place. Take your friends, who are group with you now, and go there. Inside, find Haraag. Retrieve what was stolen from us, and kill Haraag, if you can. Each bring back Artifacts, and I will reward each of you for them.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  5,  66 /* LockFellow */, 0, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  6,  17 /* LocalBroadcast */, 0, 1, NULL, 'Chief Taraash raises his hands, and a soft glow infuses %tn''s Fellowship.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  7,  64 /* TellFellow */, 1, 1, NULL, 'Now, go. And remember, if you leave group, you''ll need new group and my blessing to enter hole again.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
