VALUES (@parent_id,  0,   5 /* Motion */, 1, 1, 0x13000087 /* Wave */, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  1,  10 /* Tell */, 1, 1, NULL, 'Woe is me! The Hea must have overheard that I have been sending travelers to investigate their caverns, for they invaded my house, burned down my apple trees, and ate my bunny rabbits!  I barely escaped here to safety of Redspire in one piece! Fortunately my dear friend Micon has agreed to give me shelter until I am back upon my feet.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  2,  10 /* Tell */, 1, 1, NULL, 'They must hold that relic in high esteem indeed. A most fanciful folk, I must say! I heard that it is a legendary spear that they dismantled and enshrined in two separate caverns on the island. It once belonged to a warrior from an opposite faction, I believe. He tried to persuade them back to traditional ways, but they would have none of it. Somehow they dispatched him but retained his spear...which is supposed to have some sort of magical, "living" properties to it. I''m not quite sure what to believe.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  3,  10 /* Tell */, 1, 1, NULL, 'Perhaps the answer could be found in the caverns to the northwest along the shore.  Try the area around 40.2N 84.7W --at least that''s where I saw the Tumeroks heading last. Beware, friend--it''s quite likely to be well guarded. I''d go myself, but I must catch up on my journal today. If you would like to read it, here are a few pages that I wrote the other day.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (@parent_id,  4,   3 /* Give */, 1, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0 /* Undef */, 11975 /* Bachus Flufen's Journal */, 1, 0 /* Undef */, 0, False, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);

