VALUES (@parent_id,  0,  10 /* Tell */, 0, 1, NULL, 'Friend, welcome to our lines. I am Sergeant Brigitta, second in command of the defense here. We''re managing to keep Kryst safe, but there are things you could do to help us against the Mosswarts.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
