VALUES (@parent_id,  0,  13 /* TextDirect */, 0, 1, NULL, 'You think, "The man standing before me looked haggard. As soon as he saw me approaching, he started shaking like a leaf getting ready to be shed in the autumn air."', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
